const { propagation, context } = require('@opentelemetry/api');

function setupContext() {
  const baggage = propagation.createBaggage({
    'session.id': { value: process.env.SESSION_ID || 'unknown-session' }
    // Adicione outras informações contextuais aqui
  });

  context.with(propagation.setBaggage(context.active(), baggage), () => {
    console.log("Context setup completed.");
  });
}

module.exports = { setupContext };
